CREATE DEFINER = 'root'@'localhost'
PROCEDURE proc_getProductInfo(IN user_id int)
BEGIN
  SELECT
    p.*,
    u.uploadTime,
    c.state,
    c.city,
    e.cost,
    t.remark,
    r.pattern
  FROM tbl_product p
    LEFT JOIN tbl_uploadhistory u
      ON u.pid = p.id AND u.userId = user_id
    LEFT JOIN tbl_city c
      ON c.id = p.cityID
    LEFT JOIN tbl_express e
      ON e.cityID = p.cityID AND e.userId = user_id
    LEFT JOIN tbl_unit t
      ON p.unit = t.unit
    LEFT JOIN tbl_exchange_rate r
      ON r.unitID = t.id AND r.userID = user_id;
END
$$

