                   $item = explode('=', $param);                    $this->datas[$item[0]] = urlencode($item[1]);                }                $this->dataParam = "{\"SeasonId\":\"{$this->datas['SeasonId']}\"," .                        "\"CollectionId\":\"{$this->datas['CollectionId']}\",\"ItemId\":\"{$this->datas['ItemId']}\"," .                        "\"VendorColorId\":\"{$this->datas['VendorColorId']}\",\"SeasonMemoCode\":\"{$this->datas['SeasonMemoCode']}\"," .                        "\"GenderMemoCode\":\"{$this->datas['GenderMemoCode']}\"," .                        "\"Language\":\"ZH\",\"CountryId\":\"HK\",\"SubLineMemoCode\":\"\",\"CategoryId\":0," .                        "\"ItemResponse\":\"\",\"MenuResponse\":\"menuResponse\",\"SizeChart\":false," .                        "\"ItemTag\":true,\"NoContext\":false}&time='{$time}'";            }        } catch (Exception $exc) {                    }    }    public function init() {        $this->info['unit'] = 'EUR';        $this->station = self::STATION;        $this->citys[] = '意大利';        $this->setMyarea();    }    private function parseJson() {        $url = self::DATA_URL . '?itemRequest=' . $this->dataParam;        $this->content = $this->curl_url_agent($url, $this->cookie_file);        if ($this->content === false) {            $this->trace('parseJson: 取得JSON失败');            return false;        }        $menuResponsePos = strpos($this->content, 'menuResponse=');        $json = substr_replace($this->content, '', $menuResponsePos);        $json = trim($json, ";");        $json = trim($json);        $json = '[' . $json . ']';        //$this->trace($json);        $this->jsonData = json_decode($json);        if ($this->jsonData === NULL) {            $this->trace('JSON数据解析失败! err=' . json_last_error());            return false;        }        //var_dump($objs);        return true;    }    //解析单个商品    public function productAnaly($url, $city = '') {  