 }        } catch (Exception $e) {                    }        return $page;    }    //搜索商品链接    private function findProductLinks($html) {        $temps = array();        $links = array();        //连接        foreach ($html->find('.picture a') as $key => $element) {            if ($key == self::MAX_P)                break;            $productid = str_replace('/product/', '', $element->href);            $temps[] = self::NET_A_PORTER . $productid;            $this->trace('搜索到商品链接:' . self::NET_A_PORTER . $productid);        }        $links = array_unique($temps);        return $links;    }    private function getSizeChartId() {        $this->SizeChartId = $this->jsonData[0]->SizeChartId;        $this->trace('SizeChartId=' . $this->SizeChartId);    }    //设置商品信息    private function setProductInfo() {        $this->getpid();        if ($this->getStock() > 0) {            $this->getBrandAndTitle();            $this->getPrice();            $this->getDetailsAndDesigner();            $this->getImages();            $this->getSizeFitContainer();        }    }    //商品编号    private function getpid() {        $sid = $this->jsonData[0]->ItemKey->ItemCode;        $this->info['pid'] = trim($sid);        $this->info['id'] = self::STATION . $this->info['pid'];        $this->trace("商品ID:" . $this->info['pid']);    }    //尺寸描述    private function getSizeFitContainer() {        $this->getSizeChartId();        $content = '';        switch ($this->SizeChartId) {            case '01':                $content = '<table><tbody><tr class=""><td class=""></td><td colspan="9" class="">尺码</td></tr><tr><td class="">I/II/III</td><td class=""></td><td class=""></td><td class=""></td><td class="">0</td><td class="">1/I</td><td class="">2/II</td><td class="">3/III</td><td class="">4/IV</td><td class="">5/V</td></tr><tr><td class="">S/M/L</td><td class=""></td><td class="">XXXS</td><td class="">XXS</td><td class="">XS/P</td><td class="">S</